void main(){	for(;;) TestMotors();}void TestMotors(){	int i;		start_press();		for(i=0;i<10;++i) {		sleep(2.0);				motor(0,100);		motor(1,100);				sleep(8.0);				motor(1,0);		motor(1,-100);				sleep(2.0);				alloff();				motor(0,-20);		motor(1,-20);				sleep(4.0);				alloff();				beep();	}}void TestTest(){	int i;		start_press();		for(i=0;i<10;++i) {		sleep(0.5);				beep();	}}